const items = $input.all();

const processedData = items.map((item) => {
  try {
    // Get AI response text (support multiple formats)
    let aiSummary = '';
    if (item.json.response?.message?.content) {
      aiSummary = item.json.response.message.content;
    } else if (item.json.message?.content) {
      aiSummary = item.json.message.content;
    } else if (item.json.output) {
      aiSummary = item.json.output;
    } else {
      aiSummary = JSON.stringify(item.json);
    }

    // Helper function: extract text between labels
    function extract(label, text = aiSummary) {
      const regex = new RegExp(`\\*\\*${label}:\\*\\*\\s*([\\s\\S]*?)(?=\\n\\n|\\n\\*\\*|$)`, "i");
      const match = text.match(regex);
      return match ? match[1].trim() : "Not found";
    }

    // Extract fields
    const quarter = extract("Quarter");
    const year = extract("Year");

    const revenue = extract("Revenue");
    const ebit = extract("EBIT / Operating Income");
    const netIncome = extract("Net Income");
    const eps = extract("EPS \\(diluted\\)");
    const cashFlowOps = extract("Cash Flow from Operations");
    const capex = extract("Capex");

    const grossMargin = extract("Gross Margin");
    const operatingMargin = extract("Operating Margin");
    const netMargin = extract("Net Margin");

    const cashInvestments = extract("Cash and Short-Term Investments");
    const totalDebt = extract("Total Debt");
    const shareholdersEquity = extract("Shareholders’ Equity");

    const guidance = extract("Management Guidance / Outlook");
    const shareholderReturns = extract("Shareholder Returns");
    const growthInitiatives = extract("Growth Initiatives");

    const strategicHighlights = extract("Strategic Highlights / Key Events");
    const risks = extract("Risks / Challenges Noted");
    const sentiment = extract("Sentiment and Qualitative Signals");
    const additionalNotes = extract("Additional Notes");

    return {
      quarter,
      year,
      revenue,
      ebit,
      net_income: netIncome,
      eps,
      cash_flow_ops: cashFlowOps,
      capex,
      gross_margin: grossMargin,
      operating_margin: operatingMargin,
      net_margin: netMargin,
      cash_investments: cashInvestments,
      total_debt: totalDebt,
      shareholders_equity: shareholdersEquity,
      guidance,
      shareholder_returns: shareholderReturns,
      growth_initiatives: growthInitiatives,
      strategic_highlights: strategicHighlights,
      risks,
      sentiment,
      additional_notes: additionalNotes,
      full_summary: aiSummary,
      processing_date: new Date().toISOString(),
      _debug: {
        summary_length: aiSummary.length,
        has_quarter: quarter !== "Not found",
        has_year: year !== "Not found"
      }
    };

  } catch (error) {
    return {
      quarter: "Error",
      year: "Error",
      revenue: "Error",
      ebit: "Error",
      net_income: "Error",
      eps: "Error",
      cash_flow_ops: "Error",
      capex: "Error",
      gross_margin: "Error",
      operating_margin: "Error",
      net_margin: "Error",
      cash_investments: "Error",
      total_debt: "Error",
      shareholders_equity: "Error",
      guidance: "Error",
      shareholder_returns: "Error",
      growth_initiatives: "Error",
      strategic_highlights: "Error",
      risks: "Error",
      sentiment: "Error",
      additional_notes: "Error",
      full_summary: "Failed to process AI response",
      processing_date: new Date().toISOString(),
      _error: error.message,
      _rawData: JSON.stringify(item, null, 2)
    };
  }
});

return processedData;

const items = $input.all();

const processedData = items.map((item) => {
  try {
    // Get AI response text (support multiple formats)
    let aiForecast = '';
    if (item.json.response?.message?.content) {
      aiForecast = item.json.response.message.content;
    } else if (item.json.message?.content) {
      aiForecast = item.json.message.content;
    } else if (item.json.output) {
      aiForecast = item.json.output;
    } else {
      aiForecast = JSON.stringify(item.json);
    }

    // Helper function: extract text between labels (with support for bullet points and multi-line)
    function extract(label, text = aiForecast) {
      const regex = new RegExp(`\\*\\*${label}:\\*\\*\\s*([\\s\\S]*?)(?=\\n\\n|\\n\\*\\*|$)`, "i");
      const match = text.match(regex);
      return match ? match[1].trim() : "Not found";
    }

    // Helper function: extract bullet point values from margin section
    function extractMargin(marginLabel, text = aiForecast) {
      const regex = new RegExp(`-\\s*${marginLabel}:\\s*([^\\n]+)`, "i");
      const match = text.match(regex);
      return match ? match[1].trim() : "Not found";
    }

    // Helper function: extract bullet point values from balance sheet
    function extractBalanceSheet(itemLabel, text = aiForecast) {
      const regex = new RegExp(`-\\s*${itemLabel}:\\s*([^\\n]+)`, "i");
      const match = text.match(regex);
      return match ? match[1].trim() : "Not found";
    }

    // Extract forecast fields
    const forecastYear = extract("Forecast Year");

    const forecastRevenue = extract("Forecast Revenue");
    const forecastEBIT = extract("Forecast EBIT \\/ Operating Income");
    const forecastNetIncome = extract("Forecast Net Income");
    const forecastEPS = extract("Forecast EPS \\(diluted\\)");
    const forecastCashFlowOps = extract("Forecast Cash Flow from Operations");
    const forecastCapex = extract("Forecast Capex");

    // Extract margin forecasts from bullet points
    const forecastGrossMargin = extractMargin("Gross Margin", aiForecast);
    const forecastOperatingMargin = extractMargin("Operating Margin", aiForecast);
    const forecastNetMargin = extractMargin("Net Margin", aiForecast);

    // Extract balance sheet outlook from bullet points
    const cashInvestmentsOutlook = extractBalanceSheet("Cash and Short-Term Investments", aiForecast);
    const totalDebtOutlook = extractBalanceSheet("Total Debt", aiForecast);
    const shareholdersEquityOutlook = extractBalanceSheet("Shareholders’ Equity", aiForecast);

    // Extract key sections
    const keyDrivers = extract("Key Drivers \\/ Assumptions");
    const risksToForecast = extract("Risks to Forecast");
    const analystViewpoint = extract("Analyst Viewpoint");

    // Clean up the data for Google Sheets (remove markdown formatting, etc.)
    const cleanValue = (value) => {
      if (value === "Not found") return "";
      return value.replace(/\*\*/g, "").replace(/\*/g, "").trim();
    };

    return {
      forecast_year: cleanValue(forecastYear),
      forecast_revenue: cleanValue(forecastRevenue),
      forecast_ebit: cleanValue(forecastEBIT),
      forecast_net_income: cleanValue(forecastNetIncome),
      forecast_eps: cleanValue(forecastEPS),
      forecast_cash_flow_ops: cleanValue(forecastCashFlowOps),
      forecast_capex: cleanValue(forecastCapex),
      forecast_gross_margin: cleanValue(forecastGrossMargin),
      forecast_operating_margin: cleanValue(forecastOperatingMargin),
      forecast_net_margin: cleanValue(forecastNetMargin),
      cash_investments_outlook: cleanValue(cashInvestmentsOutlook),
      total_debt_outlook: cleanValue(totalDebtOutlook),
      shareholders_equity_outlook: cleanValue(shareholdersEquityOutlook),
      key_drivers: cleanValue(keyDrivers),
      risks_to_forecast: cleanValue(risksToForecast),
      analyst_viewpoint: cleanValue(analystViewpoint),
      full_forecast: aiForecast.replace(/\*\*/g, "").replace(/\*/g, ""), // Clean markdown from full text
      processing_date: new Date().toISOString(),
      _debug: {
        forecast_length: aiForecast.length,
        has_forecast_year: forecastYear !== "Not found",
        has_financial_metrics: forecastRevenue !== "Not found" || forecastEBIT !== "Not found"
      }
    };

  } catch (error) {
    return {
      forecast_year: "Error",
      forecast_revenue: "Error",
      forecast_ebit: "Error",
      forecast_net_income: "Error",
      forecast_eps: "Error",
      forecast_cash_flow_ops: "Error",
      forecast_capex: "Error",
      forecast_gross_margin: "Error",
      forecast_operating_margin: "Error",
      forecast_net_margin: "Error",
      cash_investments_outlook: "Error",
      total_debt_outlook: "Error",
      shareholders_equity_outlook: "Error",
      key_drivers: "Error",
      risks_to_forecast: "Error",
      analyst_viewpoint: "Error",
      full_forecast: "Failed to process forecast response",
      processing_date: new Date().toISOString(),
      _error: error.message,
      _rawData: JSON.stringify(item, null, 2)
    };
  }
});

return processedData;
